void leint ()
{
    int lista [1024],i=0;
    do {
        scanf ("%d",&lista[i]);
        if (lista[i]==0)
        {
            printSoma (lista,i+1);
        }
        i++;
    }while (lista [i-1]!=0);
}
void printSoma (int L[],int size)
{
    int i,soma=0;
    for (i = 0; i < size; ++i)
    {
        soma = soma + L[i];
    }
    printf("%d",soma);
}

