void segMaior (int L[],int size)
{
    int i,maior,segmaior;
    if (L[0]>=L[1])
    {
        maior =L[0];
        segmaior=L[1];
    }
    else
    {
        segmaior =L[0];
        maior=L[1];
    }
    for (i=1;i<size;i++)
    {
        if (L[i]> maior)
        {
            segmaior=maior;
            maior = L[i];
        }
    }
    printf("%d", segmaior);
}

